clear allclcfunction re = f(x)  re = sin(2*x) + 0.1*sin(15*x);endfunctionfunction re = g(x)  re = sin(2*x) + 0.1*cos(15*x);endfunctionfunction re = h(x)  re = g(x).*f(x);endfunctionfunction re = E(x)  re = (sin(2*x)).^2 + 0.1*sin(2*x).*cos(15*x) + 0.1*sin(2*x).*sin(15*x) + sin(30*x)/200;endfunctionfunction re = get_real(fk_hat, x)  k = [[0:1:size(fk_hat)(2)-1],[0],[-(size(fk_hat)(2)-1):1:-1]];  re = zeros(size(x)(1), size(x)(2));  for i = 1:size(fk_hat(2))    re += fk_hat(i).*exp(j*k(i)*x)  endforendfunctionfunction re = remove_last(x)  L = size(x)(2);  re = x(1:L-1);endfunctionPI = 3.1415926;h_x = 2*PI/32;x = 0:h_x:2*PI;x = remove_last(x);x_ref = 0:0.05:2*PI;fj = f(x);gj = g(x);hj = h(x);H_hat = fft(hj);f_hat = fft(fj);g_hat = fft(gj);h_hat = zeros(1,32);for k = 1:32   for m = 1:32      #h_hat(k) +=    endforendforfigure(1)plot(x_ref, h(x_ref))hold plot(x_ref, get_real(H_hat, x_ref))figure(2)plot(x_ref, h(x_ref))holdplot(x_ref, get_real(h_hat, x_ref))figure(3)plot(x_ref, h(x_ref))holdplot(x_ref, get_real(