clear allclcfunction re = f(x)  re = sin(2*x) + 0.1*sin(15*x);endfunctionfunction re = g(x)  re = sin(2*x) + 0.1*cos(15*x);endfunctionfunction re = h(x)  re = g(x).*f(x);endfunctionfunction re = remove_last(x)  L = size(x)(2);  re = x(1:L-1);endfunctionPI = 3.1415926;h_x = 2*PI/32;x = 0:h_x:2*PI;x = remove_last(x);x_ref = 0:0.01:2*PI;fj = f(x);gj = g(x);hj = h(x);H_hat = fft(hj);f_hat = fft(fj);g_hat = fft(gj);h_hat = zeros(1,32);for k = 1:32   for m = 1:32      #h_hat(k) +=    endforendforfigure(1)plot(x_ref, h(x_ref))hold 