clear allclctheda = 0:0.1:2*3.1415926;esize = size(theda);function y = f(x)  theda = 0:0.1:2*3.1415926;  esize = size(theda);  y = zeros(1, esize(2));  for k = 1:esize(2)    y(k) = 1 + x(k) + x(k)^2/2 - exp(j*theda(k));  endforendfunction[x_1, fval, info] = fsolve (@f, zeros(1, esize(2)));figure(1)plot(real(x_1),imag(x_1))title("stability diagram for 2nd R-K")figure(2)plot(real(x_2), imag(x_1))title("stability diagram for 4th R-K")