clear allclcfunction solving(f1, f2, f3, x)  for i =  1:(length(x)-1)    f1k1 = h*(-f1.*f3);    f2k1 = h*f1;    f3k1 = h*f3;    f1k2 = h*(-(f1k1/2+f1).*(f3k1/2+f3));    f2k2 = h*(f1+f1k1/2);    f3k2 = h*(f2+f2k1/2);    f1k3 = h*(-(f1k2/2+f1).*(f3k2/2+f3));    f2k3 = h*(f1+f1k2/2);    f3k3 = h*(f2+f2k2/2);    f1k4 = h*(-(f1k3/2+f1).*(f3k3/2+f3));    f2k4 = h*(f1+f1k3/2);    f3k4 = h*(f2+f2k3/2);    f1 = f1 + f1k1/6 + f1k2/3 + f1k3/3 + f1k4/6;    f2 = f2 + f2k1/6 + f2k2/3 + f2k3/3 + f2k4/6;    f3 = f3 + f3k1/6 + f3k2/3 + f3k3/3 + f3k4/6;  endforendfunctionf1i = [1,0.5];f1cpy = f1i;h = 0.01;f2 = zeros(1,2);f3 = zeros(1,2);x = 0:h:10;alpha = 2;while (abs(f2(1) - 1) > 1e-5)  f1 = f1cpy;  f2 = zeros(1,2);  f3 = zeros(1,2);    fcpy = f1cpy;  f1cpy(1) = fcpy(2); f1cpy(2) = fcpy(1);  fcpy = f1cpy;  f1cpy(1) = fcpy(1) + (fcpy(1) - fcpy(2)) / (f2(2) - f2(1)) * (1-f2(2));  alpha += 1;endwhile