clear allclcPI = 3.1415926;h4 = PI/4;h8 = PI/8;h16 = PI/16;h32 = PI/32;x4 = [0:h4:PI];x8 = [0:h8:PI];x16 = [0:h16:PI];x32 = [0:h32:PI];I = 2;I4 = I8 = I16 = I32 = 0;for i = 2:4   I4 += h4^3 /12 * sin(x4(i)) + h4*sin(x4(i));endforfor i = 2:8   I8 += h8^3 /12 * sin(x8(i)) + h8*sin(x8(i));endforfor i = 2:16   I16 += h16^3 /12 * sin(x16(i)) + h16*sin(x16(i));endforfor i = 2:32   I32 += h32^3 /12 * sin(x32(i)) + h32*sin(x32(i));endforhf = figure();plot(log(h4),log(I4-I));holdplot(log(h8),log(I8-I));plot(log(h16),log(I16-I));plot(log(h32),log(I32-I));title("Part C");xlabel("log(h)");ylabel("log(error)");print(hf,"Part_C.png","-dpng");slope = (log(I4-I)-log(I8-I))/(log(h4)-log(h8));