clear allclcfunction re = f(x)  re =log(x)/x;endfunctionh = (8-1)/8;x = [1:h:8];exact = (log(8)^2)/2;#Simpson's rulesimpson = h/3*f(x(1));simpson += h/3*f(x(2))*4;for i = 2:4   simpson += h/3*f(x(2*i))*4;   simpson += h/3*f(x(2*i-1))*2;endforsimpson += h/3*f(x(9));#Gauss[x_g, w] = gauss_leg(1,8,9);gauss = 0;for i = 1:9    gauss += w(i) * f(x_g(i));endforerror_s = abs(simpson - exact);error_g = abs(gauss - exact);